function getTodayIndex() {
  const now = new Date();
  const localDate = new Date(now.getTime() + 8 * 60 * 60 * 1000); // 转为北京时间
  const start = new Date(Date.UTC(localDate.getUTCFullYear(), 0, 0));
  const diff = localDate - start;
  const oneDay = 1000 * 60 * 60 * 24;
  return Math.floor(diff / oneDay);
}

function updatePage() {
  const i = getTodayIndex();
  const q = quotes[i % quotes.length];
  const food = foods[i % foods.length];
  const act = actions[i % actions.length];

  const now = new Date();
  const localDate = new Date(now.getTime() + 8 * 60 * 60 * 1000); // 转为北京时间
  const dateStr = localDate.toLocaleDateString('zh-CN', {
    year: 'numeric', month: 'long', day: 'numeric'
  });

  document.getElementById("today").innerText = "今天是：" + dateStr;
  document.getElementById("quote").innerText = "✨ " + q[0] + "\n" + q[1];
  document.getElementById("food").innerText = "🍙 今日幸运食物：" + food;
  document.getElementById("action").innerText = "🪄 今天可以：" + act;
}

function toggleBGM() {
  const audio = document.getElementById("bgm");
  audio.paused ? audio.play() : audio.pause();
}

window.onload = updatePage;
